import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ProgressBar from "react-bootstrap/ProgressBar";
import "./css/Learn.css";

const Learn = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [list, setList] = useState(null);
    const [progress, setProgress] = useState(0);
    const [wordStats, setWordStats] = useState({});
    const [currentWordIndex, setCurrentWordIndex] = useState(0);
    const [currentExerciseType, setCurrentExerciseType] = useState("QCM");
    const [options, setOptions] = useState([]);
    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const [isCorrect, setIsCorrect] = useState(null);
    const [userInput, setUserInput] = useState("");
    const [hasSubmitted, setHasSubmitted] = useState(false);

    useEffect(() => {
        fetch(`http://localhost:5000/api/lists`)
            .then(response => response.json())
            .then(data => {
                const selectedList = data.find(l => l.id === parseInt(id));
                if (selectedList && selectedList.words.length > 0) {
                    setList(selectedList);
                    initializeWordStats(selectedList.words);

                    // D√©finition du mode par d√©faut
                    const initialMode = "QCM_Term"; // ou "QCM_Definition" selon la logique souhait√©e
                    generateOptions(selectedList.words[0], selectedList.words, initialMode);
                } else {
                    console.error("Liste non trouv√©e ou vide.");
                }
            })
            .catch(error => console.error("Erreur de chargement de la liste", error));
    }, [id]);


    const initializeWordStats = (words) => {
        const stats = {};
        words.forEach((word, index) => {
            stats[index] = {
                termCorrectCount: 0, // Compte les bonnes r√©ponses lorsque l'utilisateur doit trouver la d√©finition
                definitionCorrectCount: 0, // Compte les bonnes r√©ponses lorsque l'utilisateur doit trouver le terme
                usedRecently: false
            };
        });
        setWordStats(stats);
    };

    const generateOptions = (word, words, mode) => {
        console.log("üîÑ Appel de generateOptions avec :", { word, mode });

        if (!word || !words) {
            console.warn("‚ùå Probl√®me : word ou words est undefined !");
            return;
        }

        let correctAnswer;
        let incorrectAnswers;

        if (mode === "QCM_Term") {
            correctAnswer = word.definition;
            incorrectAnswers = words
                .map(w => w.definition)
                .filter(def => def !== correctAnswer);
        } else if (mode === "QCM_Definition") {
            correctAnswer = word.term;
            incorrectAnswers = words
                .map(w => w.term)
                .filter(term => term !== correctAnswer);
        } else {
            console.warn("‚ùå Mode inconnu :", mode);
            return;
        }

        if (incorrectAnswers.length < 3) {
            console.warn("‚ö†Ô∏è Pas assez d'options incorrectes !");
        }

        incorrectAnswers = incorrectAnswers
            .sort(() => Math.random() - 0.5)
            .slice(0, 3);

        const allOptions = [correctAnswer, ...incorrectAnswers]
            .sort(() => Math.random() - 0.5);

        console.log("‚úÖ Options g√©n√©r√©es :", allOptions);

        setOptions(allOptions);
        setSelectedAnswer(null);
        setIsCorrect(null);
    };


    const selectNextWord = () => {
        console.log("üîÑ Appel de selectNextWord");

        if (!list || !list.words || Object.keys(wordStats).length === 0) {
            console.warn("‚ùå Impossible de s√©lectionner un mot !");
            return;
        }

        let availableWords = Object.keys(wordStats).filter(i => !wordStats[i].usedRecently);

        if (availableWords.length === 0) {
            Object.keys(wordStats).forEach(i => (wordStats[i].usedRecently = false));
            availableWords = Object.keys(wordStats);
        }

        let nextIndex = availableWords[Math.floor(Math.random() * availableWords.length)];
        wordStats[nextIndex].usedRecently = true;
        setWordStats({ ...wordStats });
        setCurrentWordIndex(parseInt(nextIndex));

        const stats = wordStats[nextIndex];

        let exerciseType;
        let mode;

        if (stats.termCorrectCount < 2 && stats.definitionCorrectCount < 2) {
            exerciseType = stats.termCorrectCount <= stats.definitionCorrectCount ? "QCM_Term" : "QCM_Definition";
            mode = stats.termCorrectCount <= stats.definitionCorrectCount ? "QCM_Term" : "QCM_Definition";
        } else if (stats.termCorrectCount < 2) {
            exerciseType = "QCM_Term";
            mode = "QCM_Term";
        } else if (stats.definitionCorrectCount < 2) {
            exerciseType = "QCM_Definition";
            mode = "QCM_Definition";
        } else {
            exerciseType = "Input";
            mode = "Input";
        }

        console.log(`üéØ Nouvel exercice : ${exerciseType} pour "${list.words[nextIndex].term}"`);

        setCurrentExerciseType(exerciseType);
        generateOptions(list.words[nextIndex], list.words, mode);
    };




    const handleQCMChoice = (selectedOption) => {
        if (!list) return;

        let correctAnswer;
        let statsField;

        if (currentExerciseType === "QCM_Term") {
            correctAnswer = list.words[currentWordIndex].definition;
            statsField = "termCorrectCount";
        } else if (currentExerciseType === "QCM_Definition") {
            correctAnswer = list.words[currentWordIndex].term;
            statsField = "definitionCorrectCount";
        } else {
            console.error("üö® Probl√®me : type d'exercice inconnu !", currentExerciseType);
            return;
        }


        const isAnswerCorrect = selectedOption === correctAnswer;
        setSelectedAnswer(selectedOption);
        setIsCorrect(isAnswerCorrect);

        if (isAnswerCorrect) {
            setWordStats(prevStats => ({
                ...prevStats,
                [currentWordIndex]: {
                    ...prevStats[currentWordIndex],
                    [statsField]: prevStats[currentWordIndex][statsField] + 1
                }
            }));
        }


        setTimeout(() => {
            selectNextWord();
        }, 1000);
    };


    const handleInputSubmit = () => {
        if (!userInput.trim()) return;

        setHasSubmitted(true);

        let correctAnswer;
        let statsField;

        if (currentExerciseType === "Input") {
            correctAnswer = list.words[currentWordIndex].term;
            statsField = "termCorrectCount";  // üî• ERREUR corrig√©e ici
        } else {
            return;
        }

        const isAnswerCorrect = userInput.trim().toLowerCase() === correctAnswer.toLowerCase();
        setIsCorrect(isAnswerCorrect);

        if (isAnswerCorrect) {
            wordStats[currentWordIndex][statsField] += 1;
            setWordStats({ ...wordStats });
        }

        setTimeout(() => {
            selectNextWord();
            setUserInput("");
            setIsCorrect(null);
            setHasSubmitted(false);
        }, 1000);
    };


    if (!list) return <p>Chargement...</p>;

    return (
        <div className="learn-container">
            <button className="exit-button" onClick={() => navigate(`/list/${id}`)}>
                <i className="bi bi-x-lg"></i>
            </button>

            <ProgressBar now={progress} label={`${Math.round(progress)}% completed`} />

            <h2 className="term">{list.words[currentWordIndex].term}</h2>

            {currentExerciseType === "QCM" ? (
                <div className="options-container">
                    {options.length > 0 ? (
                        options.map((option, index) => (
                            <button
                                key={index}
                                className={`option-button ${selectedAnswer === option ? (isCorrect ? "correct" : "incorrect") : ""}`}
                                onClick={() => handleQCMChoice(option)}
                                disabled={selectedAnswer !== null}
                            >
                                {option}
                                {selectedAnswer === option && (
                                    <i className={`bi ${isCorrect ? "bi-check-lg" : "bi-x"}`} />
                                )}
                            </button>
                        ))
                    ) : (
                        <p>Chargement des options...</p>
                    )}
                </div>
            ) : (
                <div className="input-container">
                    <input
                        type="text"
                        value={userInput}
                        onChange={(e) => setUserInput(e.target.value)}
                        placeholder="Write the answer"
                    />
                    <button onClick={handleInputSubmit}>
                        <i className="bi bi-arrow-up-circle-fill"></i>
                    </button>
                    {hasSubmitted && (
                        <p className={isCorrect ? "correct-text" : "incorrect-text"}>
                            {isCorrect ? "Correct !" : `False, the good answer is : ${list.words[currentWordIndex].definition}`}
                        </p>
                    )}
                </div>
            )}
        </div>
    );
};

export default Learn;
