import React, { useState } from "react";
import "./Home.css";

const Home = () => {
    const [showPopup, setShowPopup] = useState(false);
    const [newList, setNewList] = useState({ title: "", words: [] });

    // Gérer le changement de titre de la liste
    const handleChange = (e) => {
        setNewList({ ...newList, title: e.target.value });
    };

    // Ajouter un terme/définition à la liste
    const handleAddWord = () => {
        setNewList({
            ...newList,
            words: [...newList.words, { term: "", definition: "" }]
        });
    };

    // Gérer le changement des inputs des mots
    const handleWordChange = (index, e) => {
        const { name, value } = e.target;
        const updatedWords = [...newList.words];
        updatedWords[index][name] = value;
        setNewList({ ...newList, words: updatedWords });
    };

    // Gérer la soumission du formulaire
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Nouvelle liste :", newList);
        setShowPopup(false);
    };

    return (
        <div className="lists-container">
            <div>
                <div className="titleHome">Lists :</div>
                <button className="add-button" onClick={togglePopup}>
                    <ion-icon name="add-circle-outline" size="large"></ion-icon>
                    Add list
                </button>
            </div>

            {showPopup && (
                <div className="popup-overlay">
                    <div className="popup-content">
                        <h3>Create a list</h3>
                        <form onSubmit={handleSubmit}>
                            <input
                                type="text"
                                name="title"
                                placeholder="Title"
                                value={newList.title}
                                onChange={handleChange}
                                required
                            />

                            {/* Affichage des cartes (terme/définition) */}
                            {newList.words.map((word, index) => (
                                <div key={index} className="word-card">
                                    <input
                                        type="text"
                                        name="term"
                                        placeholder="Terme"
                                        value={word.term}
                                        onChange={(e) => handleWordChange(index, e)}
                                        required
                                    />
                                    <input
                                        type="text"
                                        name="definition"
                                        placeholder="Définition"
                                        value={word.definition}
                                        onChange={(e) => handleWordChange(index, e)}
                                        required
                                    />
                                </div>
                            ))}

                            {/* Bouton pour ajouter un mot */}
                            <button type="button" onClick={handleAddWord} className="add-word-button">
                                +
                            </button>

                            {/* Bouton pour valider la liste */}
                            <button type="submit">Enregistrer</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
