import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";

const Home = () => {
    const [lists, setLists] = useState([]);
    const [showPopup, setShowPopup] = useState(false);
    const [newList, setNewList] = useState({
        title: "",
        words: [{ term: "", definition: "" }, { term: "", definition: "" }],
    });

    const navigate = useNavigate();

    // Charger les listes depuis l'API
    useEffect(() => {
        fetch("http://localhost:5000/api/lists")
            .then((response) => response.json())
            .then((data) => setLists(data))
            .catch((error) => console.error("Erreur lors du chargement des listes", error));
    }, []);

    // Fonction pour naviguer vers List.js avec l'ID de la liste
    const handleListClick = (id) => {
        navigate(`/list/${id}`);
    };

    return (
        <div className="lists-container">
            <div>
                <div className="titleHome">Lists :</div>
                <button className="add-button" onClick={() => setShowPopup(!showPopup)}>
                    <ion-icon name="add-circle-outline" size="large"></ion-icon>
                    Add list
                </button>
            </div>
            <div className="lists-grid">
                {lists.map((list) => (
                    <div
                        key={list.id}
                        className="list-card"
                        onClick={() => handleListClick(list.id)}
                        style={{ cursor: "pointer" }}
                    >
                        <h3>{list.title}</h3>
                        <p>{list.words.length} termes</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
