import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./List.css";

const List = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [list, setList] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:5000/api/lists`)
            .then((response) => response.json())
            .then((data) => {
                const foundList = data.find((l) => l.id === parseInt(id));
                console.log("Liste récupérée :", foundList); // 🔍 Vérifier la structure des données
                setList(foundList);
            })
            .catch((error) => console.error("Erreur de chargement de la liste", error));
    }, [id]);


    if (!list) return <p>Chargement...</p>;

    return (
        <div className="list-container">
            <button className="back-button" onClick={() => navigate("/")}>
                <i className="bi bi-arrow-left"></i>
            </button>
            <h2>{list.title}</h2>
            <ul>
                {list.words.map((word, index) => (
                    <li key={index}>
                        <strong>{word.term}</strong>: {word.definition}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default List;
