import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ProgressBar from "react-bootstrap/ProgressBar";
import "./css/Learn.css";

const Learn = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [list, setList] = useState(null);
    const [progress, setProgress] = useState(0);
    const [wordStats, setWordStats] = useState({});
    const [currentWordIndex, setCurrentWordIndex] = useState(0);
    const [currentExerciseType, setCurrentExerciseType] = useState("QCM");
    const [options, setOptions] = useState([]);
    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const [isCorrect, setIsCorrect] = useState(null);
    const [userInput, setUserInput] = useState("");

    useEffect(() => {
        fetch(`http://localhost:5000/api/lists`)
            .then(response => response.json())
            .then(data => {
                const selectedList = data.find(l => l.id === parseInt(id));
                if (selectedList && selectedList.words.length > 0) {
                    setList(selectedList);
                    initializeWordStats(selectedList.words);
                    generateOptions(selectedList.words[0], selectedList.words);
                } else {
                    console.error("Liste non trouvée ou vide.");
                }
            })
            .catch(error => console.error("Erreur de chargement de la liste", error));
    }, [id]);

    const initializeWordStats = (words) => {
        const stats = {};
        words.forEach((word, index) => {
            stats[index] = { correctCount: 0, usedRecently: false };
        });
        setWordStats(stats);
    };

    const generateOptions = (word, words) => {
        if (!word || !words) return; // Vérification de sécurité

        const correctDefinition = word.definition;
        let incorrectDefs = words
            .map(w => w.definition)
            .filter(def => def !== correctDefinition);

        incorrectDefs = incorrectDefs.sort(() => 0.5 - Math.random()).slice(0, 3);
        const allOptions = [correctDefinition, ...incorrectDefs].sort(() => 0.5 - Math.random());

        setOptions(allOptions);
        setSelectedAnswer(null);
        setIsCorrect(null);
    };

    const selectNextWord = () => {
        if (!list || !list.words || Object.keys(wordStats).length === 0) return; // Vérification de sécurité

        let availableWords = Object.keys(wordStats).filter(i => !wordStats[i].usedRecently);

        // Si tous les mots ont été utilisés récemment, on les réinitialise
        if (availableWords.length === 0) {
            Object.keys(wordStats).forEach(i => (wordStats[i].usedRecently = false));
            availableWords = Object.keys(wordStats);
        }

        let nextIndex = availableWords[Math.floor(Math.random() * availableWords.length)];

        // Vérification pour éviter un accès à undefined
        if (nextIndex === undefined) {
            console.error("Aucun mot disponible pour l'exercice.");
            return;
        }

        wordStats[nextIndex].usedRecently = true;
        setWordStats({ ...wordStats });
        setCurrentWordIndex(parseInt(nextIndex));

        const nextWordStats = wordStats[nextIndex];

        if (nextWordStats.correctCount >= 2) {
            setCurrentExerciseType("Input");
        } else {
            setCurrentExerciseType("QCM");
            generateOptions(list.words[nextIndex], list.words);
        }
    };


    const handleQCMChoice = (selectedDef) => {
        if (!list) return; // Vérification de sécurité

        const correctDef = list.words[currentWordIndex].definition;
        const isAnswerCorrect = selectedDef === correctDef;

        setSelectedAnswer(selectedDef);
        setIsCorrect(isAnswerCorrect);

        if (isAnswerCorrect) {
            wordStats[currentWordIndex].correctCount += 1;
            setWordStats({ ...wordStats });
        }

        setTimeout(() => {
            selectNextWord();
        }, 1000);
    };

    const handleInputSubmit = () => {
        if (!list) return; // Vérification de sécurité

        const correctDef = list.words[currentWordIndex].definition;
        const isAnswerCorrect = userInput.trim() === correctDef;

        setIsCorrect(isAnswerCorrect);

        setTimeout(() => {
            selectNextWord();
            setUserInput("");
        }, 1000);
    };

    if (!list) return <p>Chargement...</p>;

    return (
        <div className="learn-container">
            <button className="exit-button" onClick={() => navigate(`/list/${id}`)}>
                <i className="bi bi-x-lg"></i>
            </button>

            <ProgressBar now={progress} label={`${Math.round(progress)}% completed`} />

            <h2 className="term">{list.words[currentWordIndex].term}</h2>

            {currentExerciseType === "QCM" ? (
                <div className="options-container">
                    {options.length > 0 ? (
                        options.map((option, index) => (
                            <button
                                key={index}
                                className={`option-button ${
                                    selectedAnswer
                                        ? option === list.words[currentWordIndex].definition
                                            ? "correct"
                                            : option === selectedAnswer
                                                ? "incorrect"
                                                : ""
                                        : ""
                                }`}
                                onClick={() => handleQCMChoice(option)}
                                disabled={selectedAnswer !== null}
                            >
                                {option}
                                {selectedAnswer === option && (
                                    <i className={`bi ${isCorrect ? "bi-check-lg" : "bi-x"}`} />
                                )}
                            </button>
                        ))
                    ) : (
                        <p>Chargement des options...</p>
                    )}
                </div>
            ) : (
                <div className="input-container">
                    <input
                        type="text"
                        value={userInput}
                        onChange={(e) => setUserInput(e.target.value)}
                        placeholder="Écrivez la définition"
                    />
                    <button onClick={handleInputSubmit}>
                        <i className="bi bi-arrow-up-circle-fill"></i>
                    </button>
                    {isCorrect !== null && (
                        <p className={isCorrect ? "correct-text" : "incorrect-text"}>
                            {isCorrect ? "Correct !" : `Faux, la bonne réponse était : ${list.words[currentWordIndex].definition}`}
                        </p>
                    )}
                </div>
            )}
        </div>
    );
};

export default Learn;
