import React, { useState, useEffect } from "react";
import "./Home.css"

const Home = () => {
    const [showPopup, setShowPopup] = useState(false);
    const [lists, setLists] = useState([]);
    const [newList, setNewList] = useState({
        title: "",
        words: [],
    });

    useEffect(() => {
        fetch("http://localhost:5000/api/lists")
            .then((response) => response.json())
            .then((data) => {
                console.log("Données reçues :", data);
                setLists(data);
            })
            .catch((error) => console.error("Erreur lors du chargement des listes", error));
    }, []);

    // Ouvrir/fermer la popup
    const togglePopup = () => {
        setShowPopup(!showPopup);
    };


    return (
        <div>
            <div className="titleHome" >Lists :</div>
            <button className="add-button" onClick={togglePopup}>
                <ion-icon name="add-circle-outline" size="large"></ion-icon> Add list
            </button>
        </div>
    );
};

{show}

export default Home;
