import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./Cards.css";

const Cards = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    // Liste des mots (exemple, à remplacer par des données dynamiques)
    const words = [
        { term: "Mot 1", definition: "Définition 1" },
        { term: "Mot 2", definition: "Définition 2" },
        { term: "Mot 3", definition: "Définition 3" },
    ];

    // États pour suivre la progression et les scores
    const [currentIndex, setCurrentIndex] = useState(0);
    const [flipped, setFlipped] = useState(false);
    const [correctCount, setCorrectCount] = useState(0);
    const [incorrectCount, setIncorrectCount] = useState(0);

    // Fonction pour retourner la carte
    const handleFlip = () => {
        setFlipped(!flipped);
    };

    // Fonction pour gérer la réponse (correcte ou incorrecte)
    const handleAnswer = (isCorrect) => {
        if (isCorrect) {
            setCorrectCount(correctCount + 1);
        } else {
            setIncorrectCount(incorrectCount + 1);
        }

        // Passer à la carte suivante
        if (currentIndex < words.length - 1) {
            setCurrentIndex(currentIndex + 1);
            setFlipped(false);
        }
    };

    return (
        <div className="card-container">
            {/* Bouton de sortie */}
            <button className="exit-button" onClick={() => navigate(`/list/${id}`)}>
                <i className="bi bi-x-lg"></i>
            </button>

            {/* Compteur de progression */}
            <div className="progress-counter">{currentIndex + 1}/{words.length}</div>

            {/* Compteurs de réponses */}
            <div className="score-container">
                <span className="incorrect-score">{incorrectCount}</span>
                <span className="correct-score">{correctCount}</span>
            </div>

            {/* Carte retournable */}
            <div className={`flip-card ${flipped ? "flipped" : ""}`} onClick={handleFlip}>
                <div className="flip-card-inner">
                    {/* Face avant */}
                    <div className="flip-card-front">
                        <div className="card-content">{words[currentIndex].term}</div>
                    </div>
                    {/* Face arrière */}
                    <div className="flip-card-back">
                        <div className="card-content">{words[currentIndex].definition}</div>
                    </div>
                </div>
            </div>

            {/* Boutons de validation */}
            <div className="button-group">
                <button className="incorrect-button" onClick={() => handleAnswer(false)}>
                    <i className="bi bi-x-lg"></i>
                </button>
                <button className="correct-button" onClick={() => handleAnswer(true)}>
                    <i className="bi bi-check-lg"></i>
                </button>
            </div>
        </div>
    );
};

export default Cards;
