import React, {useState, useEffect} from "react";
import "./Home.css"

const Home = () => {
    const [showPopup, setShowPopup] = useState(false);
    const [lists, setLists] = useState([]);
    const [newList, setNewList] = useState({
        title: "",
        words: [],
    });

    // Charger list depuis l'API
    useEffect(() => {
        fetch("http://localhost:5000/api/lists")
            .then((response) => response.json())
            .then((data) => {
                setLists(data);
            })
            .catch((error) => console.error("Erreur lors du chargement des listes", error));
    }, []);

    // Ouvrir/fermer la popup
    const togglePopup = () => {
        setShowPopup(!showPopup);
    };

    // Gérer les changements dans le formulaire
    const handleChange = (e) => {
        const { title, value } = e.target;
        setNewList({ ...newList, [title]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        fetch("http://localhost:5000/api/lists", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(newList),
        })
            .then((res) => res.json())
        .then((data) => {
            setLists([...lists, data]);
            setShowPopup(false);
            setNewList({title: "", words: []});
        })
        .catch((error) => console.error("Erreur ajout liste:", error));
    }


    return (
        <div className="lists-container">
            <div>
                <div className="titleHome">Lists :</div>
                <button className="add-button" onClick={togglePopup}>
                    <ion-icon name="add-circle-outline" size="large"></ion-icon>
                    Add list
                </button>
            </div>

            {showPopup && (
                <div className="popup-overlay">
                    <div className="popup-">
                        <h3>Add a list</h3>
                        <form onSubmit={handleSubmit}>
                            <input
                                type={"text"}
                                name={"title"}
                                placeholder={"Enter title"}
                                value={newList.title}
                                onChange={handleChange}
                                required
                            />

                        </form>
                    </div>
                </div>

            )}
        </div>
    );
};


export default Home;
