import React, { useState, useEffect } from "react";
import "./Home.css";

const Home = () => {
    const [showPopup, setShowPopup] = useState(false);
    const [lists, setLists] = useState([]);
    const [newList, setNewList] = useState({ title: "", words: [] });

    // Charger les listes depuis l'API
    useEffect(() => {
        fetch("http://localhost:5000/api/lists")
            .then((response) => response.json())
            .then((data) => setLists(data))
            .catch((error) => console.error("Erreur lors du chargement des listes", error));
    }, []);

    // Ouvrir la popup avec 2 cartes par défaut
    const togglePopup = () => {
        if (!showPopup) {
            setNewList({
                title: "",
                words: [
                    { term: "", definition: "" },
                    { term: "", definition: "" }
                ],
            });
        }
        setShowPopup(!showPopup);
    };

    // Gérer le changement du titre
    const handleChange = (e) => {
        setNewList({ ...newList, title: e.target.value });
    };

    // Ajouter un mot (terme/définition)
    const handleAddWord = () => {
        setNewList({
            ...newList,
            words: [...newList.words, { term: "", definition: "" }],
        });
    };

    // Modifier un terme ou une définition
    const handleWordChange = (index, e) => {
        const { name, value } = e.target;
        const updatedWords = [...newList.words];
        updatedWords[index][name] = value;
        setNewList({ ...newList, words: updatedWords });
    };

    // Envoyer la liste à l'API
    const handleSubmit = (e) => {
        e.preventDefault();

        fetch("http://localhost:5000/api/lists", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newList),
        })
            .then((res) => res.json())
            .then((data) => {
                setLists([...lists, data]);
                setShowPopup(false);
                setNewList({ title: "", words: [] }); // Réinitialisation
            })
            .catch((error) => console.error("Erreur ajout liste:", error));
    };

    return (
        <div className="lists-container">
            <div>
                <div className="titleHome">Lists :</div>
                <button className="add-button" onClick={togglePopup}>
                    <ion-icon name="add-circle-outline" size="large"></ion-icon>
                    Add list
                </button>
            </div>

            {/* Popup d'ajout de liste */}
            {showPopup && (
                <div className="popup-overlay">
                    <div className="popup-content">
                        <h3>Create a list</h3>
                        <form onSubmit={handleSubmit}>
                            {/* Input du titre */}
                            <input
                                type="text"
                                name="title"
                                placeholder="Title"
                                value={newList.title}
                                onChange={handleChange}
                                required
                            />

                            {/* Affichage des cartes (terme/définition) */}
                            {newList.words.map((word, index) => (
                                <div key={index} className="word-card">
                                    <input
                                        type="text"
                                        name="term"
                                        placeholder="Terme"
                                        value={word.term}
                                        onChange={(e) => handleWordChange(index, e)}
                                        required
                                    />
                                    <input
                                        type="text"
                                        name="definition"
                                        placeholder="Definition"
                                        value={word.definition}
                                        onChange={(e) => handleWordChange(index, e)}
                                        required
                                    />
                                </div>
                            ))}

                            {/* Bouton "+" pour ajouter une nouvelle carte */}
                            <button type="button" onClick={handleAddWord} className="add-word-button">
                                </button>

                            {/* Bouton pour soumettre la liste */}
                            <button type="submit">Enregistrer</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
