import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Carousel } from 'react-bootstrap'; // Importation du Carousel
import "./List.css";

const List = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [list, setList] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:5000/api/lists`)
            .then((response) => response.json())
            .then((data) => {
                const foundList = data.find((l) => l.id === parseInt(id));
                setList(foundList);
            })
            .catch((error) => console.error("Erreur de chargement de la liste", error));
    }, [id]);

    if (!list) return <p>Chargement...</p>;

    return (
        <div className="list-container">
            <button className="back-button" onClick={() => navigate("/")}>
                <i className="bi bi-arrow-left"></i>
            </button>
            <h2>{list.title}</h2>

            {/* Carrousel contenant chaque terme de la liste */}
            <Carousel>
                {list.words.map((word, index) => (
                    <Carousel.Item key={index}>
                        <div className="carousel-item-content">
                            <h3>{word.term}</h3>
                            {/* On peut ajouter plus de contenu ici pour la d√©finition ou d'autres infos */}
                        </div>
                    </Carousel.Item>
                ))}
            </Carousel>
        </div>
    );
};

export default List;
