import React, {useEffect, useState} from "react";
import {useParams, useNavigate} from "react-router-dom";
import {Carousel, Card} from 'react-bootstrap'; // Importation de Card de react-bootstrap
import "./List.css";

const List = () => {
    const {id} = useParams();
    const navigate = useNavigate();
    const [list, setList] = useState(null);
    const [flippedCards, setFlippedCards] = useState({});

    useEffect(() => {
        fetch(`http://localhost:5000/api/lists`)
            .then((response) => response.json())
            .then((data) => {
                const foundList = data.find((l) => l.id === parseInt(id));
                setList(foundList);
            })
            .catch((error) => console.error("Erreur de chargement de la liste", error));
    }, [id]);

    // Fonction pour basculer l'état "retourné" d'une carte
    const handleFlip = (index) => {
        setFlippedCards((prev) => ({
            ...prev,
            [index]: !prev[index],
        }));
    };

    if (!list) return <p>Chargement...</p>;

    return (
        <div className="list-container">
            <button className="back-button" onClick={() => navigate("/")}>
                <i className="bi bi-arrow-left"></i>
            </button>

            {/* Carrousel contenant chaque terme de la liste */}
            <Carousel>
                {list.words.map((word, index) => (
                    <Carousel.Item key={index}>
                        <div className="carousel-item-content">
                            <Card className="custom-card">
                                <Card.Body className="d-flex justify-content-center align-items-center">
                                    <h3 className="term-text">{word.term}</h3>
                                </Card.Body>
                            </Card>
                        </div>
                    </Carousel.Item>
                ))}
            </Carousel>
            <div className="button-group">
                <button className="cards-button">
                    <i className="bi bi-front"></i>
                    Cards
                </button>

                <button className="cards-button">
                    <i className="bi bi-graph-up-arrow"></i>
                    Learn
                </button>

                <button className="cards-button">
                    <i className="bi bi-mortarboard-fill"></i>
                    Test
                </button>
            </div>

        </div>
    );
};

export default List;
